const axios = require('axios');
const fs = require('fs');
const path = require('path');

async function convertToAnime(imagePath, modelId = 5) {
    try {
        // 1. Baca gambar dan konversi ke base64
        const imageBuffer = fs.readFileSync(imagePath);
        const base64Image = `data:image/jpeg;base64,${imageBuffer.toString('base64')}`;

        // 2. Nilai settings yang ditangkap dari browser (mungkin perlu diperbarui jika kedaluwarsa)
        const settings = "";

        console.log('Memulai konversi...');
        const createResponse = await axios.post('https://aienhancer.ai/api/v1/r/image-enhance/create', {
            model: modelId,
            image: base64Image,
            settings: settings
        }, {
            headers: {
                'Content-Type': 'application/json',
                'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36',
                'Origin': 'https://aienhancer.ai',
                'Referer': 'https://aienhancer.ai/photo-to-anime-converter'
            }
        });

        const taskId = createResponse.data.data.id;
        console.log(`Tugas dibuat. Task ID: ${taskId}`);

        // 3. Polling untuk hasil
        let resultUrl = null;
        while (!resultUrl) {
            console.log('Menunggu hasil...');
            const resultResponse = await axios.post('https://aienhancer.ai/api/v1/r/image-enhance/result', {
                task_id: taskId
            }, {
                headers: {
                    'Content-Type': 'application/json',
                    'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36',
                    'Origin': 'https://aienhancer.ai',
                    'Referer': 'https://aienhancer.ai/photo-to-anime-converter'
                }
            });

            const status = resultResponse.data.data.status;
            const output = resultResponse.data.data.output;
            if (output) {
                resultUrl = output;
                console.log('Konversi berhasil!');
            }
            if (status === 'success') {
                resultUrl = resultResponse.data.data.output;
                console.log('Konversi berhasil!');
            } else if (status === 'failed') {
                throw new Error('Konversi gagal di server.');
            } else {
                // Tunggu 2 detik sebelum polling lagi
                await new Promise(resolve => setTimeout(resolve, 2000));
            }
        }

        // 4. Download hasil
        console.log(`Mendownload hasil dari: ${resultUrl}`);
        const downloadResponse = await axios.get(resultUrl, { responseType: 'arraybuffer' });
        const outputPath = path.join(path.dirname(imagePath), 'result_anime.jpg');
        fs.writeFileSync(outputPath, downloadResponse.data);
        console.log(`Hasil disimpan di: ${outputPath}`);

        return outputPath;
    } catch (error) {
        console.error('Terjadi kesalahan:', error.response ? error.response.data : error.message);
    }
}

// Jalankan pengujian
const testImagePath = process.argv[2] || './test_image.jpg';
convertToAnime(testImagePath);
